  `RESOURCE_URL` VARCHAR(256) DEFAULT NULL COMMENT '资源请求路径',
  `RESOURCE_TYPE` VARCHAR(16) NOT NULL COMMENT '资源类型 M:菜单 P:页面 O:按钮',
